INSERT INTO `zhihu` (`url`, `question_title`, `question_text`, `followers`, `views`, `likes`, `topics1`, `topics2`, `topics3`, `topics4`) VALUES ('https://www.zhihu.com/question/522609635', '锻炼身体是早上好还是晚上好，为什么？', '锻炼身体很重要，但肯定不是每天优先级最高的任务，当你完成优先级最高的事务，剩下有时间再去做，因此取决于你有没有时间，而不是什么时间点，因为什么时间点锻炼效果都差不多。\n好好休息比锻炼身体优先级更高，要分得清事情的轻重缓急，不要因为一时热血而过度锻炼，过度锻炼只会造成反效果。', '9', '1656', '0', '锻炼', '身体健康', '养身健身', '跳绳 健身');
