INSERT INTO `zhihu` (`url`, `question_title`, `question_text`, `followers`, `views`, `likes`, `topics1`, `topics2`, `topics3`, `topics4`) VALUES ('https://www.zhihu.com/question/645431827', '现在的你，每天能花15分钟用来锻炼身体吗？', '平均每天花50分钟来撸铁，\n平时会骑自行车去学校或者图书馆或者超市来回一般20分钟。\n每天几乎雷打不动去健身，胸/臀腿/肩/背/有氧五个训练项目轮着来，健身房滞留时间一般都在一个小时及以上。\n养了猫之后还会每天花至少半个小时陪猫疯玩，和她们玩躲猫猫或者用逗猫棒在家里到处跑。\n我的运动环可以证明什么叫做我卷我自己：', '7', '2449', '0', '锻炼', '身体', '养身健身', '跳绳 健身');
INSERT INTO `zhihu` (`url`, `question_title`, `question_text`, `followers`, `views`, `likes`, `topics1`, `topics2`, `topics3`, `topics4`) VALUES ('https://www.zhihu.com/question/428366901', '怎样在身体不好的情况下兼顾学习?', '中考生，因为颈椎病修了一年学，开学后学习压力大后颈椎病又来干扰，头晕，记忆力也下降，学习很吃力，晚上因为身体心理原因思前想后，很焦虑烦躁。没有多久就要…', '15', '7858', '0', '身体状态', '养身健身', NULL, NULL);
